import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import { 
  insertMiningRigSchema,
  insertPortfolioBalanceSchema,
  insertMiningTransactionSchema,
  insertExchangeConnectionSchema,
  insertPaymentSchema,
  type WebSocketMessage,
  type PriceData,
  type MiningStatsData
} from "@shared/schema";

const COINGECKO_API_URL = "https://api.coingecko.com/api/v3";

export async function registerRoutes(app: Express): Promise<Server> {
  const httpServer = createServer(app);

  // WebSocket server for real-time updates
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  let priceData: PriceData = {
    bitcoin: { usd: 43287.50, usd_24h_change: 2.45 },
    ethereum: { usd: 2834.21, usd_24h_change: -1.23 }
  };

  // Broadcast to all connected clients
  function broadcast(message: WebSocketMessage) {
    wss.clients.forEach((client) => {
      if (client.readyState === WebSocket.OPEN) {
        client.send(JSON.stringify(message));
      }
    });
  }

  // Fetch real cryptocurrency prices
  async function updatePrices() {
    try {
      const response = await fetch(
        `${COINGECKO_API_URL}/simple/price?ids=bitcoin,ethereum&vs_currencies=usd&include_24hr_change=true`
      );

      if (response.ok) {
        const data = await response.json();
        priceData = {
          bitcoin: { 
            usd: data.bitcoin.usd, 
            usd_24h_change: data.bitcoin.usd_24hr_change || 0 
          },
          ethereum: { 
            usd: data.ethereum.usd, 
            usd_24h_change: data.ethereum.usd_24hr_change || 0 
          }
        };

        broadcast({
          type: 'price_update',
          data: priceData
        });
      }
    } catch (error) {
      console.error('Failed to fetch prices:', error);
    }
  }

  // Update mining statistics and simulate earnings
  async function updateMiningStats() {
    try {
      const defaultUserId = "default-user";
      const rigs = await storage.getMiningRigs(defaultUserId);

      // Calculate aggregate stats
      const stats: MiningStatsData = {
        totalHashRate: rigs.reduce((sum, rig) => sum + (rig.isActive ? rig.hashRate : 0), 0),
        activeMinerCount: rigs.filter(rig => rig.isActive).length,
        totalDailyEarnings: rigs.reduce((sum, rig) => sum + (rig.isActive ? rig.dailyEarnings || 0 : 0), 0),
        totalPowerConsumption: rigs.reduce((sum, rig) => sum + (rig.isActive ? rig.powerConsumption : 0), 0)
      };

      // Simulate small mining rewards every update
      for (const rig of rigs.filter(r => r.isActive)) {
        const randomReward = Math.random() * 0.001; // Small random amount
        const usdValue = randomReward * (rig.cryptocurrency === 'BTC' ? priceData.bitcoin.usd : priceData.ethereum.usd);

        if (Math.random() > 0.7) { // 30% chance of reward each update
          await storage.createMiningTransaction(defaultUserId, {
            rigId: rig.id,
            type: "mining_reward",
            cryptocurrency: rig.cryptocurrency,
            amount: randomReward,
            usdValue: usdValue
          });

          // Update portfolio balance
          const currentBalance = await storage.getPortfolioBalance(defaultUserId, rig.cryptocurrency);
          if (currentBalance) {
            await storage.updatePortfolioBalance(defaultUserId, {
              cryptocurrency: rig.cryptocurrency,
              amount: currentBalance.amount + randomReward
            });
          }
        }
      }

      broadcast({
        type: 'mining_update',
        data: stats
      });

    } catch (error) {
      console.error('Failed to update mining stats:', error);
    }
  }

  wss.on('connection', (ws) => {
    console.log('WebSocket client connected');

    // Send current data to new client
    ws.send(JSON.stringify({
      type: 'price_update',
      data: priceData
    }));

    ws.on('close', () => {
      console.log('WebSocket client disconnected');
    });
  });

  // Start periodic updates
  updatePrices(); // Initial fetch
  updateMiningStats(); // Initial update
  setInterval(updatePrices, 30000); // Update prices every 30 seconds
  setInterval(updateMiningStats, 60000); // Update mining stats every minute

  // API Routes

  // Get current cryptocurrency prices
  app.get("/api/prices", async (req, res) => {
    res.json(priceData);
  });

  // Get mining rigs for user
  app.get("/api/mining-rigs", async (req, res) => {
    try {
      const userId = "default-user"; // In real app, get from auth
      const rigs = await storage.getMiningRigs(userId);
      res.json(rigs);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch mining rigs" });
    }
  });

  // Create new mining rig
  app.post("/api/mining-rigs", async (req, res) => {
    try {
      const userId = "default-user";
      const rigData = insertMiningRigSchema.parse(req.body);
      const rig = await storage.createMiningRig(userId, rigData);
      res.json(rig);
    } catch (error) {
      res.status(400).json({ error: "Invalid mining rig data" });
    }
  });

  // Update mining rig
  app.patch("/api/mining-rigs/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const updates = req.body;
      const rig = await storage.updateMiningRig(id, updates);

      if (!rig) {
        return res.status(404).json({ error: "Mining rig not found" });
      }

      res.json(rig);
    } catch (error) {
      res.status(400).json({ error: "Failed to update mining rig" });
    }
  });

  // Delete mining rig
  app.delete("/api/mining-rigs/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const deleted = await storage.deleteMiningRig(id);

      if (!deleted) {
        return res.status(404).json({ error: "Mining rig not found" });
      }

      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: "Failed to delete mining rig" });
    }
  });

  // Get portfolio balances
  app.get("/api/portfolio", async (req, res) => {
    try {
      const userId = "default-user";
      const balances = await storage.getPortfolioBalances(userId);
      res.json(balances);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch portfolio" });
    }
  });

  // Get mining transactions
  app.get("/api/transactions", async (req, res) => {
    try {
      const userId = "default-user";
      const limit = parseInt(req.query.limit as string) || 10;
      const transactions = await storage.getMiningTransactions(userId, limit);
      res.json(transactions);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch transactions" });
    }
  });

  // Get exchange connections
  app.get("/api/exchanges", async (req, res) => {
    try {
      const userId = "default-user";
      const connections = await storage.getExchangeConnections(userId);
      res.json(connections);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch exchange connections" });
    }
  });

  // Update exchange connection
  app.post("/api/exchanges", async (req, res) => {
    try {
      const userId = "default-user";
      const connectionData = insertExchangeConnectionSchema.parse(req.body);
      const connection = await storage.createOrUpdateExchangeConnection(userId, connectionData);
      res.json(connection);
    } catch (error) {
      res.status(400).json({ error: "Invalid exchange connection data" });
    }
  });

  // Calculate mining profitability
  app.post("/api/calculate-profitability", async (req, res) => {
    try {
      const { cryptocurrency, hashRate, hashRateUnit, powerConsumption, electricityCost } = req.body;

      if (!cryptocurrency || !hashRate || !powerConsumption || !electricityCost) {
        return res.status(400).json({ error: "Missing required parameters" });
      }

      const price = cryptocurrency === 'BTC' ? priceData.bitcoin.usd : priceData.ethereum.usd;

      // Simplified profitability calculation
      // In reality, this would use network difficulty, block rewards, etc.
      const baseRevenuePerTH = cryptocurrency === 'BTC' ? 0.000015 : 0.00005; // BTC/ETH per TH/s per day
      let normalizedHashRate = hashRate;

      // Convert to standard units (TH/s for BTC, MH/s for ETH)
      if (hashRateUnit === 'GH/s' && cryptocurrency === 'BTC') {
        normalizedHashRate = hashRate / 1000;
      } else if (hashRateUnit === 'MH/s' && cryptocurrency === 'BTC') {
        normalizedHashRate = hashRate / 1000000;
      } else if (hashRateUnit === 'TH/s' && cryptocurrency === 'ETH') {
        normalizedHashRate = hashRate * 1000; // Convert to MH/s for ETH
      }

      const dailyCryptoReward = normalizedHashRate * baseRevenuePerTH;
      const dailyRevenue = dailyCryptoReward * price;
      const dailyPowerCostKwh = (powerConsumption * 24) / 1000;
      const dailyCosts = dailyPowerCostKwh * electricityCost;
      const dailyProfit = dailyRevenue - dailyCosts;

      res.json({
        revenue: dailyRevenue,
        costs: dailyCosts,
        profit: dailyProfit,
        cryptoAmount: dailyCryptoReward
      });
    } catch (error) {
      res.status(500).json({ error: "Failed to calculate profitability" });
    }
  });

  // Get payments for user
  app.get("/api/payments", async (req, res) => {
    try {
      const userId = "default-user";
      const limit = parseInt(req.query.limit as string) || 10;
      const payments = await storage.getPayments(userId, limit);
      res.json(payments);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch payments" });
    }
  });

  // Create new payment
  app.post("/api/payments", async (req, res) => {
    try {
      const userId = "default-user";
      const FIXED_PAYMENT_ADDRESS = "0x23fc41bec78cc36ef1646c1910ccf7c89ffcfdb1";

      // Validate request data
      const validatedData = insertPaymentSchema.parse(req.body);

      // Enforce fixed payment address for security
      const paymentData = {
        ...validatedData,
        toAddress: FIXED_PAYMENT_ADDRESS // Override any client-provided address
      };

      const payment = await storage.createPayment(userId, paymentData);
      res.json(payment);
    } catch (error) {
      res.status(400).json({ error: "Invalid payment data" });
    }
  });

  // Update payment status
  app.patch("/api/payments/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const { status, transactionHash } = req.body;

      if (!status) {
        return res.status(400).json({ error: "Status is required" });
      }

      // Validate status is one of allowed values
      const validStatuses = ["pending", "confirmed", "failed"];
      if (!validStatuses.includes(status)) {
        return res.status(400).json({ error: "Invalid status. Must be pending, confirmed, or failed" });
      }

      const payment = await storage.updatePaymentStatus(id, status, transactionHash);

      if (!payment) {
        return res.status(404).json({ error: "Payment not found" });
      }

      res.json(payment);
    } catch (error) {
      res.status(500).json({ error: "Failed to update payment" });
    }
  });

  return httpServer;
}

server/storage.ts.content:
import { 
  type User, 
  type InsertUser, 
  type MiningRig,
  type InsertMiningRig,
  type PortfolioBalance,
  type InsertPortfolioBalance,
  type MiningTransaction,
  type InsertMiningTransaction,
  type ExchangeConnection,
  type InsertExchangeConnection,
  type Payment,
  type InsertPayment
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Mining rig operations
  getMiningRigs(userId: string): Promise<MiningRig[]>;
  getMiningRig(id: string): Promise<MiningRig | undefined>;
  createMiningRig(userId: string, rig: InsertMiningRig): Promise<MiningRig>;
  updateMiningRig(id: string, updates: Partial<MiningRig>): Promise<MiningRig | undefined>;
  deleteMiningRig(id: string): Promise<boolean>;

  // Portfolio operations
  getPortfolioBalances(userId: string): Promise<PortfolioBalance[]>;
  getPortfolioBalance(userId: string, cryptocurrency: string): Promise<PortfolioBalance | undefined>;
  updatePortfolioBalance(userId: string, balance: InsertPortfolioBalance): Promise<PortfolioBalance>;

  // Transaction operations
  getMiningTransactions(userId: string, limit?: number): Promise<MiningTransaction[]>;
  createMiningTransaction(userId: string, transaction: InsertMiningTransaction): Promise<MiningTransaction>;

  // Exchange operations
  getExchangeConnections(userId: string): Promise<ExchangeConnection[]>;
  getExchangeConnection(userId: string, exchange: string): Promise<ExchangeConnection | undefined>;
  createOrUpdateExchangeConnection(userId: string, connection: InsertExchangeConnection): Promise<ExchangeConnection>;

  // Payment operations
  getPayments(userId: string, limit?: number): Promise<Payment[]>;
  getPayment(id: string): Promise<Payment | undefined>;
  createPayment(userId: string, payment: InsertPayment): Promise<Payment>;
  updatePaymentStatus(id: string, status: string, transactionHash?: string): Promise<Payment | undefined>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private miningRigs: Map<string, MiningRig>;
  private portfolioBalances: Map<string, PortfolioBalance>;
  private miningTransactions: Map<string, MiningTransaction>;
  private exchangeConnections: Map<string, ExchangeConnection>;
  private payments: Map<string, Payment>;

  constructor() {
    this.users = new Map();
    this.miningRigs = new Map();
    this.portfolioBalances = new Map();
    this.miningTransactions = new Map();
    this.exchangeConnections = new Map();
    this.payments = new Map();
    this.initializeDefaultData();
  }

  private initializeDefaultData() {
    // Create default user
    const defaultUser: User = {
      id: "default-user",
      username: "john_miner",
      password: "hashedpassword"
    };
    this.users.set(defaultUser.id, defaultUser);

    // Create some mining rigs
    const btcRig: MiningRig = {
      id: randomUUID(),
      userId: defaultUser.id,
      name: "BTC Miner #1",
      model: "Antminer S19 Pro",
      cryptocurrency: "BTC",
      hashRate: 110,
      hashRateUnit: "TH/s",
      powerConsumption: 3250,
      isActive: true,
      dailyEarnings: 18.43,
      createdAt: new Date()
    };

    const ethRig: MiningRig = {
      id: randomUUID(),
      userId: defaultUser.id,
      name: "ETH Miner #1",
      model: "RTX 4090 Rig",
      cryptocurrency: "ETH",
      hashRate: 420,
      hashRateUnit: "MH/s",
      powerConsumption: 1800,
      isActive: true,
      dailyEarnings: 12.87,
      createdAt: new Date()
    };

    this.miningRigs.set(btcRig.id, btcRig);
    this.miningRigs.set(ethRig.id, ethRig);

    // Create portfolio balances
    const btcBalance: PortfolioBalance = {
      id: randomUUID(),
      userId: defaultUser.id,
      cryptocurrency: "BTC",
      amount: 0.0456,
      lastUpdated: new Date()
    };

    const ethBalance: PortfolioBalance = {
      id: randomUUID(),
      userId: defaultUser.id,
      cryptocurrency: "ETH",
      amount: 0.8234,
      lastUpdated: new Date()
    };

    const usdBalance: PortfolioBalance = {
      id: randomUUID(),
      userId: defaultUser.id,
      cryptocurrency: "USD",
      amount: 540.36,
      lastUpdated: new Date()
    };

    this.portfolioBalances.set(btcBalance.id, btcBalance);
    this.portfolioBalances.set(ethBalance.id, ethBalance);
    this.portfolioBalances.set(usdBalance.id, usdBalance);

    // Create some transactions
    const btcTransaction: MiningTransaction = {
      id: randomUUID(),
      userId: defaultUser.id,
      rigId: btcRig.id,
      type: "mining_reward",
      cryptocurrency: "BTC",
      amount: 0.0023,
      usdValue: 99.46,
      timestamp: new Date(Date.now() - 2 * 60 * 60 * 1000) // 2 hours ago
    };

    const ethTransaction: MiningTransaction = {
      id: randomUUID(),
      userId: defaultUser.id,
      rigId: ethRig.id,
      type: "mining_reward",
      cryptocurrency: "ETH",
      amount: 0.045,
      usdValue: 127.54,
      timestamp: new Date(Date.now() - 4 * 60 * 60 * 1000) // 4 hours ago
    };

    this.miningTransactions.set(btcTransaction.id, btcTransaction);
    this.miningTransactions.set(ethTransaction.id, ethTransaction);

    // Create exchange connections
    const coinbaseConnection: ExchangeConnection = {
      id: randomUUID(),
      userId: defaultUser.id,
      exchange: "coinbase",
      isConnected: true,
      apiKeyId: "encrypted_coinbase_key",
      settings: { autoSellETH: true, autoSellBTC: false },
      lastSync: new Date()
    };

    const binanceConnection: ExchangeConnection = {
      id: randomUUID(),
      userId: defaultUser.id,
      exchange: "binance",
      isConnected: true,
      apiKeyId: "encrypted_binance_key",
      settings: { autoSellETH: true, autoSellBTC: false },
      lastSync: new Date()
    };

    this.exchangeConnections.set(coinbaseConnection.id, coinbaseConnection);
    this.exchangeConnections.set(binanceConnection.id, binanceConnection);
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.username === username);
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  async getMiningRigs(userId: string): Promise<MiningRig[]> {
    return Array.from(this.miningRigs.values()).filter(rig => rig.userId === userId);
  }

  async getMiningRig(id: string): Promise<MiningRig | undefined> {
    return this.miningRigs.get(id);
  }

  async createMiningRig(userId: string, rigData: InsertMiningRig): Promise<MiningRig> {
    const id = randomUUID();
    const rig: MiningRig = { 
      ...rigData, 
      id, 
      userId, 
      createdAt: new Date(),
      dailyEarnings: 0,
      isActive: rigData.isActive ?? true
    };
    this.miningRigs.set(id, rig);
    return rig;
  }

  async updateMiningRig(id: string, updates: Partial<MiningRig>): Promise<MiningRig | undefined> {
    const rig = this.miningRigs.get(id);
    if (!rig) return undefined;

    const updatedRig = { ...rig, ...updates };
    this.miningRigs.set(id, updatedRig);
    return updatedRig;
  }

  async deleteMiningRig(id: string): Promise<boolean> {
    return this.miningRigs.delete(id);
  }

  async getPortfolioBalances(userId: string): Promise<PortfolioBalance[]> {
    return Array.from(this.portfolioBalances.values()).filter(balance => balance.userId === userId);
  }

  async getPortfolioBalance(userId: string, cryptocurrency: string): Promise<PortfolioBalance | undefined> {
    return Array.from(this.portfolioBalances.values()).find(
      balance => balance.userId === userId && balance.cryptocurrency === cryptocurrency
    );
  }

  async updatePortfolioBalance(userId: string, balanceData: InsertPortfolioBalance): Promise<PortfolioBalance> {
    const existing = await this.getPortfolioBalance(userId, balanceData.cryptocurrency);

    if (existing) {
      const updated = { ...existing, ...balanceData, lastUpdated: new Date() };
      this.portfolioBalances.set(existing.id, updated);
      return updated;
    } else {
      const id = randomUUID();
      const balance: PortfolioBalance = {
        ...balanceData,
        id,
        userId,
        lastUpdated: new Date()
      };
      this.portfolioBalances.set(id, balance);
      return balance;
    }
  }

  async getMiningTransactions(userId: string, limit: number = 10): Promise<MiningTransaction[]> {
    return Array.from(this.miningTransactions.values())
      .filter(transaction => transaction.userId === userId)
      .sort((a, b) => (b.timestamp?.getTime() || 0) - (a.timestamp?.getTime() || 0))
      .slice(0, limit);
  }

  async createMiningTransaction(userId: string, transactionData: InsertMiningTransaction): Promise<MiningTransaction> {
    const id = randomUUID();
    const transaction: MiningTransaction = {
      ...transactionData,
      id,
      userId,
      timestamp: new Date(),
      rigId: transactionData.rigId ?? null
    };
    this.miningTransactions.set(id, transaction);
    return transaction;
  }

  async getExchangeConnections(userId: string): Promise<ExchangeConnection[]> {
    return Array.from(this.exchangeConnections.values()).filter(conn => conn.userId === userId);
  }

  async getExchangeConnection(userId: string, exchange: string): Promise<ExchangeConnection | undefined> {
    return Array.from(this.exchangeConnections.values()).find(
      conn => conn.userId === userId && conn.exchange === exchange
    );
  }

  async createOrUpdateExchangeConnection(userId: string, connectionData: InsertExchangeConnection): Promise<ExchangeConnection> {
    const existing = await this.getExchangeConnection(userId, connectionData.exchange);

    if (existing) {
      const updated = { ...existing, ...connectionData, lastSync: new Date() };
      this.exchangeConnections.set(existing.id, updated);
      return updated;
    } else {
      const id = randomUUID();
      const connection: ExchangeConnection = {
        ...connectionData,
        id,
        userId,
        lastSync: new Date(),
        isConnected: connectionData.isConnected ?? false,
        apiKeyId: connectionData.apiKeyId ?? null,
        settings: connectionData.settings ?? null
      };
      this.exchangeConnections.set(id, connection);
      return connection;
    }
  }

  async getPayments(userId: string, limit: number = 10): Promise<Payment[]> {
    return Array.from(this.payments.values())
      .filter(payment => payment.userId === userId)
      .sort((a, b) => (b.timestamp?.getTime() || 0) - (a.timestamp?.getTime() || 0))
      .slice(0, limit);
  }

  async getPayment(id: string): Promise<Payment | undefined> {
    return this.payments.get(id);
  }

  async createPayment(userId: string, paymentData: InsertPayment): Promise<Payment> {
    const id = randomUUID();
    const payment: Payment = {
      ...paymentData,
      id,
      userId,
      timestamp: new Date(),
      status: paymentData.status || "pending",
      fromAddress: paymentData.fromAddress || null,
      transactionHash: paymentData.transactionHash || null,
      purpose: paymentData.purpose || null
    };
    this.payments.set(id, payment);
    return payment;
  }

  async updatePaymentStatus(id: string, status: string, transactionHash?: string): Promise<Payment | undefined> {
    const payment = this.payments.get(id);
    if (!payment) return undefined;

    const updated = { 
      ...payment, 
      status,
      ...(transactionHash && { transactionHash })
    };
    this.payments.set(id, updated);
    return updated;
  }
}

export const storage = new MemStorage();

