import { useState } from "react";
import { useMutation, useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { Payment } from "@shared/schema";

const PAYMENT_ADDRESS = "0x23fc41bec78cc36ef1646c1910ccf7c89ffcfdb1";

interface PaymentFormData {
  network: string;
  amount: string;
  currency: string;
  purpose: string;
  fromAddress: string;
}

export function CryptoPayments() {
  const { toast } = useToast();
  const [formData, setFormData] = useState<PaymentFormData>({
    network: "ethereum",
    amount: "",
    currency: "ETH",
    purpose: "mining_purchase",
    fromAddress: ""
  });

  const { data: payments } = useQuery<Payment[]>({
    queryKey: ["/api/payments"],
  });

  const createPaymentMutation = useMutation({
    mutationFn: async (data: PaymentFormData) => {
      const paymentPayload: any = {
        network: data.network,
        amount: parseFloat(data.amount),
        currency: data.currency,
        toAddress: PAYMENT_ADDRESS,
        purpose: data.purpose,
        status: "pending"
      };

      // Only include fromAddress if it's provided (avoid sending null)
      if (data.fromAddress && data.fromAddress.trim()) {
        paymentPayload.fromAddress = data.fromAddress.trim();
      }

      return await queryClient.fetchQuery({
        queryKey: ["create-payment"],
        queryFn: async () => {
          const response = await fetch("/api/payments", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(paymentPayload),
          });
          if (!response.ok) throw new Error("Failed to create payment");
          return response.json();
        },
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/payments"] });
      setFormData({ ...formData, amount: "", fromAddress: "" });
      toast({
        title: "Payment Initiated",
        description: "Your payment has been recorded. Please send the funds to the provided address.",
      });
    },
    onError: () => {
      toast({
        title: "Payment Failed",
        description: "Failed to initiate payment. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.amount || parseFloat(formData.amount) <= 0) {
      toast({
        title: "Invalid Amount",
        description: "Please enter a valid payment amount.",
        variant: "destructive",
      });
      return;
    }
    createPaymentMutation.mutate(formData);
  };

  const copyToClipboard = async (text: string) => {
    try {
      await navigator.clipboard.writeText(text);
      toast({
        title: "Copied to Clipboard",
        description: "Payment address copied to clipboard.",
      });
    } catch (error) {
      toast({
        title: "Copy Failed",
        description: "Failed to copy address. Please copy manually.",
        variant: "destructive",
      });
    }
  };

  const getNetworkIcon = (network: string) => {
    return network === "ethereum" ? "fab fa-ethereum" : "fas fa-vector-square";
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "confirmed":
        return "bg-green-500/20 text-green-500";
      case "pending":
        return "bg-yellow-500/20 text-yellow-500";
      case "failed":
        return "bg-red-500/20 text-red-500";
      default:
        return "bg-gray-500/20 text-gray-500";
    }
  };

  const formatAddress = (address: string) => {
    return `${address.slice(0, 6)}...${address.slice(-4)}`;
  };

  return (
    <div className="space-y-6" data-testid="crypto-payments">
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Payment Form */}
        <Card data-testid="payment-form">
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <i className="fas fa-wallet text-primary"></i>
              <span>Make Crypto Payment</span>
            </CardTitle>
            <CardDescription>
              Send Ethereum or Polygon payments for mining services
            </CardDescription>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <Label className="block text-sm font-medium mb-2">Network</Label>
                <Select 
                  value={formData.network} 
                  onValueChange={(value) => setFormData({ ...formData, network: value, currency: value === "ethereum" ? "ETH" : "MATIC" })}
                  data-testid="select-network"
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="ethereum">Ethereum Network</SelectItem>
                    <SelectItem value="polygon">Polygon Network</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label className="block text-sm font-medium mb-2">Currency</Label>
                <Select 
                  value={formData.currency} 
                  onValueChange={(value) => setFormData({ ...formData, currency: value })}
                  data-testid="select-currency"
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {formData.network === "ethereum" ? (
                      <>
                        <SelectItem value="ETH">ETH</SelectItem>
                        <SelectItem value="USDC">USDC</SelectItem>
                        <SelectItem value="USDT">USDT</SelectItem>
                      </>
                    ) : (
                      <>
                        <SelectItem value="MATIC">MATIC</SelectItem>
                        <SelectItem value="USDC">USDC</SelectItem>
                        <SelectItem value="USDT">USDT</SelectItem>
                      </>
                    )}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label className="block text-sm font-medium mb-2">Amount</Label>
                <Input
                  type="number"
                  step="0.000001"
                  placeholder="0.1"
                  value={formData.amount}
                  onChange={(e) => setFormData({ ...formData, amount: e.target.value })}
                  data-testid="input-amount"
                />
              </div>

              <div>
                <Label className="block text-sm font-medium mb-2">Purpose</Label>
                <Select 
                  value={formData.purpose} 
                  onValueChange={(value) => setFormData({ ...formData, purpose: value })}
                  data-testid="select-purpose"
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="mining_purchase">Mining Hardware Purchase</SelectItem>
                    <SelectItem value="service_fee">Service Fee</SelectItem>
                    <SelectItem value="subscription">Subscription Payment</SelectItem>
                    <SelectItem value="other">Other</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label className="block text-sm font-medium mb-2">Your Wallet Address (Optional)</Label>
                <Input
                  type="text"
                  placeholder="0x..."
                  value={formData.fromAddress}
                  onChange={(e) => setFormData({ ...formData, fromAddress: e.target.value })}
                  data-testid="input-from-address"
                />
              </div>

              <Button
                type="submit"
                className="w-full"
                disabled={createPaymentMutation.isPending}
                data-testid="button-create-payment"
              >
                {createPaymentMutation.isPending ? "Creating Payment..." : "Create Payment"}
              </Button>
            </form>
          </CardContent>
        </Card>

        {/* Payment Address */}
        <Card data-testid="payment-address">
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <i className="fas fa-qrcode text-primary"></i>
              <span>Payment Address</span>
            </CardTitle>
            <CardDescription>
              Send your cryptocurrency payments to this address
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="bg-muted/30 rounded-lg p-4">
              <div className="flex items-center justify-between mb-2">
                <span className="text-sm text-muted-foreground">Payment Address:</span>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => copyToClipboard(PAYMENT_ADDRESS)}
                  data-testid="button-copy-address"
                >
                  <i className="fas fa-copy mr-1"></i>
                  Copy
                </Button>
              </div>
              <div className="font-mono text-sm break-all bg-background rounded p-2" data-testid="payment-address-text">
                {PAYMENT_ADDRESS}
              </div>
            </div>

            <div className="space-y-2">
              <div className="flex justify-between">
                <span className="text-sm text-muted-foreground">Supported Networks:</span>
              </div>
              <div className="flex space-x-2">
                <Badge variant="outline" className="flex items-center space-x-1">
                  <i className="fab fa-ethereum text-blue-500"></i>
                  <span>Ethereum</span>
                </Badge>
                <Badge variant="outline" className="flex items-center space-x-1">
                  <i className="fas fa-vector-square text-purple-500"></i>
                  <span>Polygon</span>
                </Badge>
              </div>
            </div>

            <div className="bg-yellow-500/10 border border-yellow-500/20 rounded-lg p-3">
              <div className="flex items-start space-x-2">
                <i className="fas fa-exclamation-triangle text-yellow-500 mt-0.5"></i>
                <div className="text-sm">
                  <p className="font-medium text-yellow-500">Important:</p>
                  <p className="text-muted-foreground mt-1">
                    Only send {formData.network === "ethereum" ? "Ethereum" : "Polygon"} network tokens to this address. 
                    Sending tokens from other networks may result in permanent loss.
                  </p>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Payment History */}
      <Card data-testid="payment-history">
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <i className="fas fa-history text-primary"></i>
            <span>Payment History</span>
          </CardTitle>
          <CardDescription>
            Track your recent cryptocurrency payments
          </CardDescription>
        </CardHeader>
        <CardContent>
          {payments && payments.length > 0 ? (
            <div className="space-y-3">
              {payments.map((payment) => (
                <div key={payment.id} className="flex items-center justify-between p-3 bg-muted/30 rounded-lg" data-testid={`payment-${payment.id}`}>
                  <div className="flex items-center space-x-3">
                    <div className={`w-8 h-8 rounded-lg flex items-center justify-center ${
                      payment.network === "ethereum" ? "bg-blue-500/20" : "bg-purple-500/20"
                    }`}>
                      <i className={`${getNetworkIcon(payment.network)} ${
                        payment.network === "ethereum" ? "text-blue-500" : "text-purple-500"
                      } text-sm`}></i>
                    </div>
                    <div>
                      <p className="font-medium" data-testid={`payment-amount-${payment.id}`}>
                        {payment.amount} {payment.currency}
                      </p>
                      <p className="text-xs text-muted-foreground" data-testid={`payment-purpose-${payment.id}`}>
                        {payment.purpose?.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase()) || "Payment"}
                      </p>
                    </div>
                  </div>
                  <div className="text-right">
                    <Badge className={getStatusColor(payment.status)} data-testid={`payment-status-${payment.id}`}>
                      {payment.status.charAt(0).toUpperCase() + payment.status.slice(1)}
                    </Badge>
                    {payment.transactionHash && (
                      <p className="text-xs text-muted-foreground mt-1" data-testid={`payment-hash-${payment.id}`}>
                        {formatAddress(payment.transactionHash)}
                      </p>
                    )}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8 text-muted-foreground" data-testid="no-payments">
              <i className="fas fa-wallet text-4xl mb-4 opacity-50"></i>
              <p>No payments yet</p>
              <p className="text-sm">Your payment history will appear here</p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/exchange-status.ts.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { useState } from "react";
import { queryClient } from "@/lib/queryClient";
import { Switch } from "@/components/ui/switch";
import { Button } from "@/components/ui/button";
import type { ExchangeConnection } from "@shared/schema";

export function ExchangeStatus() {
  const { data: exchanges } = useQuery<ExchangeConnection[]>({
    queryKey: ["/api/exchanges"],
  });

  const updateExchangeMutation = useMutation({
    mutationFn: async (data: { exchange: string; settings: any }) => {
      const response = await fetch("/api/exchanges", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("Failed to update exchange");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/exchanges"] });
    },
  });

  const handleToggleAutoSell = (exchange: string, crypto: string, enabled: boolean) => {
    const connection = exchanges?.find(e => e.exchange === exchange);
    if (!connection) return;

    const newSettings = {
      ...(connection.settings as Record<string, any> || {}),
      [`autoSell${crypto}`]: enabled
    };

    updateExchangeMutation.mutate({
      exchange,
      settings: newSettings
    });
  };

  const exchangeList = [
    {
      id: "coinbase",
      name: "Coinbase",
      shortName: "CB",
      color: "bg-blue-600",
      isConnected: true
    },
    {
      id: "binance",
      name: "Binance",
      shortName: "BN",
      color: "bg-yellow-500",
      isConnected: true
    }
  ];

  return (
    <div className="bg-card border border-border rounded-lg p-6" data-testid="exchange-status">
      <h2 className="text-xl font-semibold mb-6" data-testid="exchange-status-title">Exchange Connections</h2>

      <div className="space-y-4">
        {exchangeList.map((exchange) => {
          const connection = exchanges?.find(e => e.exchange === exchange.id);

          return (
            <div key={exchange.id} className="flex items-center justify-between p-3 bg-muted/30 rounded-lg" data-testid={`exchange-${exchange.id}`}>
              <div className="flex items-center space-x-3">
                <div className={`w-10 h-10 ${exchange.color} rounded-lg flex items-center justify-center`}>
                  <span className="text-white font-bold text-sm">{exchange.shortName}</span>
                </div>
                <div>
                  <p className="font-medium" data-testid={`exchange-name-${exchange.id}`}>{exchange.name}</p>
                  <p className="text-xs text-muted-foreground" data-testid={`exchange-status-${exchange.id}`}>
                    {connection?.isConnected ? 'Connected' : 'Disconnected'}
                  </p>
                </div>
              </div>
              <div className="flex items-center space-x-2">
                <div className={`w-2 h-2 rounded-full ${connection?.isConnected ? 'bg-green-500' : 'bg-red-500'}`}></div>
                <span className={`text-xs ${connection?.isConnected ? 'text-green-500' : 'text-red-500'}`} data-testid={`exchange-connection-${exchange.id}`}>
                  {connection?.isConnected ? 'Active' : 'Inactive'}
                </span>
              </div>
            </div>
          );
        })}

        <Button className="w-full mt-4" variant="outline" data-testid="button-add-exchange">
          <i className="fas fa-plus mr-2"></i>
          Add Exchange
        </Button>
      </div>

      <div className="mt-6 pt-4 border-t border-border">
        <h3 className="font-medium mb-3" data-testid="auto-trading-title">Auto-trading Settings</h3>
        <div className="space-y-3">
          {['ETH', 'BTC'].map((crypto) => {
            const ethConnection = exchanges?.find(e => e.exchange === 'coinbase');
            const isEnabled = (ethConnection?.settings as Record<string, any>)?.[`autoSell${crypto}`] || false;

            return (
              <div key={crypto} className="flex items-center justify-between" data-testid={`auto-sell-${crypto.toLowerCase()}`}>
                <span className="text-sm text-muted-foreground">Auto-sell {crypto}</span>
                <Switch
                  checked={isEnabled}
                  onCheckedChange={(checked) => handleToggleAutoSell('coinbase', crypto, checked)}
                  data-testid={`switch-auto-sell-${crypto.toLowerCase()}`}
                />
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

client/src/components/mining-operations..content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { useState } from "react";
import { queryClient } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import type { MiningRig } from "@shared/schema";

export function MiningOperations() {
  const { data: rigs, isLoading } = useQuery<MiningRig[]>({
    queryKey: ["/api/mining-rigs"],
  });

  const toggleRigMutation = useMutation({
    mutationFn: async ({ id, isActive }: { id: string; isActive: boolean }) => {
      const response = await fetch(`/api/mining-rigs/${id}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ isActive }),
      });
      if (!response.ok) throw new Error("Failed to update mining rig");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/mining-rigs"] });
    },
  });

  if (isLoading) {
    return (
      <div className="bg-card border border-border rounded-lg p-6">
        <div className="animate-pulse">
          <div className="h-6 bg-muted rounded w-48 mb-6"></div>
          <div className="space-y-4">
            {[1, 2].map((i) => (
              <div key={i} className="bg-muted/30 rounded-lg p-4 h-24"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  const getCryptoIcon = (crypto: string) => {
    return crypto === 'BTC' ? 'fab fa-bitcoin' : 'fab fa-ethereum';
  };

  const getCryptoColor = (crypto: string) => {
    return crypto === 'BTC' ? 'bg-orange-500' : 'bg-blue-500';
  };

  return (
    <div className="bg-card border border-border rounded-lg p-6" data-testid="mining-operations">
      <h2 className="text-xl font-semibold mb-6" data-testid="mining-operations-title">Mining Operations</h2>

      <div className="space-y-4">
        {rigs?.map((rig) => (
          <div key={rig.id} className="bg-muted/30 rounded-lg p-4" data-testid={`mining-rig-${rig.id}`}>
            <div className="flex items-center justify-between mb-3">
              <div className="flex items-center space-x-3">
                <div className={`w-8 h-8 ${getCryptoColor(rig.cryptocurrency)} rounded-lg flex items-center justify-center`}>
                  <i className={`${getCryptoIcon(rig.cryptocurrency)} text-white text-xs`}></i>
                </div>
                <div>
                  <p className="font-medium" data-testid={`rig-name-${rig.id}`}>{rig.name}</p>
                  <p className="text-xs text-muted-foreground" data-testid={`rig-model-${rig.id}`}>{rig.model}</p>
                </div>
              </div>
              <Badge 
                variant={rig.isActive ? "default" : "secondary"}
                className={rig.isActive ? "bg-green-500/20 text-green-500" : ""}
                data-testid={`rig-status-${rig.id}`}
              >
                {rig.isActive ? "Active" : "Inactive"}
              </Badge>
            </div>

            <div className="grid grid-cols-3 gap-4 text-sm mb-3">
              <div>
                <p className="text-muted-foreground">Hash Rate</p>
                <p className="font-medium" data-testid={`rig-hashrate-${rig.id}`}>
                  {rig.hashRate} {rig.hashRateUnit}
                </p>
              </div>
              <div>
                <p className="text-muted-foreground">Power</p>
                <p className="font-medium" data-testid={`rig-power-${rig.id}`}>{rig.powerConsumption}W</p>
              </div>
              <div>
                <p className="text-muted-foreground">24h Earnings</p>
                <p className="font-medium text-green-500" data-testid={`rig-earnings-${rig.id}`}>
                  ${rig.dailyEarnings?.toFixed(2) || "0.00"}
                </p>
              </div>
            </div>

            <div className="flex gap-2">
              <Button
                size="sm"
                variant={rig.isActive ? "destructive" : "default"}
                onClick={() => toggleRigMutation.mutate({ id: rig.id, isActive: !rig.isActive })}
                disabled={toggleRigMutation.isPending}
                data-testid={`button-toggle-rig-${rig.id}`}
              >
                {rig.isActive ? "Stop" : "Start"}
              </Button>
              <Button
                size="sm"
                variant="outline"
                data-testid={`button-settings-rig-${rig.id}`}
              >
                Settings
              </Button>
            </div>
          </div>
        ))}

        <Button 
          className="w-full mt-4" 
          variant="outline"
          data-testid="button-add-mining-rig"
        >
          <i className="fas fa-plus mr-2"></i>
          Add Mining Rig
        </Button>
      </div>
    </div>
  );
}

client/src/components/portfolio-overview.content:
import { useQuery } from "@tanstack/react-query";
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { useWebSocket } from "@/hooks/use-websocket";
import type { PortfolioBalance, MiningTransaction } from "@shared/schema";

export function PortfolioOverview() {
  const [timePeriod, setTimePeriod] = useState("30D");
  const { priceData } = useWebSocket();

  const { data: portfolio } = useQuery<PortfolioBalance[]>({
    queryKey: ["/api/portfolio"],
  });

  const { data: transactions } = useQuery<MiningTransaction[]>({
    queryKey: ["/api/transactions"],
  });

  const calculateValue = (balance: PortfolioBalance) => {
    if (balance.cryptocurrency === 'USD') return balance.amount;

    const price = balance.cryptocurrency === 'BTC' 
      ? priceData?.bitcoin.usd || 43287.50
      : priceData?.ethereum.usd || 2834.21;

    return balance.amount * price;
  };

  const portfolioItems = [
    {
      name: "Bitcoin",
      symbol: "BTC",
      balance: portfolio?.find(b => b.cryptocurrency === 'BTC'),
      icon: "fab fa-bitcoin",
      iconBg: "bg-orange-500",
      change: "+5.2%"
    },
    {
      name: "Ethereum", 
      symbol: "ETH",
      balance: portfolio?.find(b => b.cryptocurrency === 'ETH'),
      icon: "fab fa-ethereum",
      iconBg: "bg-blue-500",
      change: "+2.8%"
    },
    {
      name: "Cash",
      symbol: "USD",
      balance: portfolio?.find(b => b.cryptocurrency === 'USD'),
      icon: "fas fa-dollar-sign",
      iconBg: "bg-green-500",
      change: "Stable"
    }
  ];

  const getTransactionIcon = (type: string) => {
    switch (type) {
      case 'mining_reward':
        return 'fas fa-arrow-down';
      case 'exchange_sale':
        return 'fas fa-arrow-up';
      default:
        return 'fas fa-exchange-alt';
    }
  };

  const getTransactionColor = (type: string) => {
    switch (type) {
      case 'mining_reward':
        return 'text-green-500';
      case 'exchange_sale':
        return 'text-blue-500';
      default:
        return 'text-muted-foreground';
    }
  };

  const formatTimeAgo = (timestamp: Date) => {
    const now = new Date();
    const diff = now.getTime() - new Date(timestamp).getTime();
    const hours = Math.floor(diff / (1000 * 60 * 60));

    if (hours < 1) return "Less than 1 hour ago";
    if (hours === 1) return "1 hour ago";
    return `${hours} hours ago`;
  };

  return (
    <div className="bg-card border border-border rounded-lg p-6" data-testid="portfolio-overview">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-xl font-semibold" data-testid="portfolio-title">Portfolio Overview</h2>
        <div className="flex items-center space-x-2">
          {["7D", "30D", "90D"].map((period) => (
            <Button
              key={period}
              size="sm"
              variant={timePeriod === period ? "default" : "ghost"}
              onClick={() => setTimePeriod(period)}
              data-testid={`button-period-${period.toLowerCase()}`}
            >
              {period}
            </Button>
          ))}
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {portfolioItems.map((item) => (
          <div key={item.symbol} className="bg-muted/30 rounded-lg p-4" data-testid={`portfolio-item-${item.symbol.toLowerCase()}`}>
            <div className="flex items-center justify-between mb-3">
              <div className="flex items-center space-x-2">
                <div className={`w-6 h-6 ${item.iconBg} rounded-full flex items-center justify-center`}>
                  <i className={`${item.icon} text-white text-xs`}></i>
                </div>
                <span className="font-medium" data-testid={`portfolio-name-${item.symbol.toLowerCase()}`}>{item.name}</span>
              </div>
              <span className={`text-xs ${item.change === 'Stable' ? 'text-muted-foreground' : 'text-green-500'}`} data-testid={`portfolio-change-${item.symbol.toLowerCase()}`}>
                {item.change}
              </span>
            </div>
            <p className="text-lg font-bold" data-testid={`portfolio-amount-${item.symbol.toLowerCase()}`}>
              {item.balance ? 
                `${item.balance.amount.toFixed(item.symbol === 'USD' ? 2 : 4)} ${item.symbol}` : 
                `0.0000 ${item.symbol}`
              }
            </p>
            <p className="text-sm text-muted-foreground" data-testid={`portfolio-value-${item.symbol.toLowerCase()}`}>
              {item.balance ? 
                `â‰ˆ $${calculateValue(item.balance).toLocaleString('en-US', { minimumFractionDigits: 2 })}` : 
                "â‰ˆ $0.00"
              }
            </p>
          </div>
        ))}
      </div>

      <div className="mt-6 pt-6 border-t border-border">
        <div className="flex items-center justify-between mb-4">
          <h3 className="font-medium" data-testid="transactions-title">Recent Transactions</h3>
          <Button variant="ghost" size="sm" data-testid="button-view-all-transactions">
            View All
          </Button>
        </div>

        <div className="space-y-3">
          {transactions?.slice(0, 5).map((transaction) => (
            <div key={transaction.id} className="flex items-center justify-between py-2" data-testid={`transaction-${transaction.id}`}>
              <div className="flex items-center space-x-3">
                <div className={`w-8 h-8 ${getTransactionColor(transaction.type)}/20 rounded-lg flex items-center justify-center`}>
                  <i className={`${getTransactionIcon(transaction.type)} ${getTransactionColor(transaction.type)} text-xs`}></i>
                </div>
                <div>
                  <p className="text-sm font-medium" data-testid={`transaction-type-${transaction.id}`}>
                    {transaction.type.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase())}
                  </p>
                  <p className="text-xs text-muted-foreground" data-testid={`transaction-time-${transaction.id}`}>
                    {formatTimeAgo(transaction.timestamp || new Date())}
                  </p>
                </div>
              </div>
              <div className="text-right">
                <p className="text-sm font-medium text-green-500" data-testid={`transaction-amount-${transaction.id}`}>
                  +{transaction.amount.toFixed(6)} {transaction.cryptocurrency}
                </p>
                <p className="text-xs text-muted-foreground" data-testid={`transaction-value-${transaction.id}`}>
                  ${transaction.usdValue.toFixed(2)}
                </p>
              </div>
            </div>
          ))}

          {(!transactions || transactions.length === 0) && (
            <div className="text-center py-4 text-muted-foreground" data-testid="no-transactions">
              No transactions yet
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

client/src/components/price-tracker.tsx.content:
import { useWebSocket } from "@/hooks/use-websocket";
import { useQuery } from "@tanstack/react-query";
import type { PriceData } from "@shared/schema";

export function PriceTracker() {
  const { priceData, isConnected } = useWebSocket();

  // Fallback query in case WebSocket fails
  const { data: fallbackPrices } = useQuery<PriceData>({
    queryKey: ["/api/prices"],
    refetchInterval: 30000,
  });

  const prices = priceData || fallbackPrices;

  if (!prices) {
    return (
      <div className="lg:col-span-2 bg-card border border-border rounded-lg p-6">
        <div className="animate-pulse">
          <div className="h-6 bg-muted rounded w-48 mb-6"></div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
            <div className="bg-muted/30 rounded-lg p-4 h-24"></div>
            <div className="bg-muted/30 rounded-lg p-4 h-24"></div>
          </div>
          <div className="h-48 bg-muted/10 rounded-lg"></div>
        </div>
      </div>
    );
  }

  const cryptos = [
    {
      name: "Bitcoin",
      symbol: "BTC",
      price: prices.bitcoin.usd,
      change: prices.bitcoin.usd_24h_change,
      icon: "fab fa-bitcoin",
      iconBg: "bg-orange-500"
    },
    {
      name: "Ethereum",
      symbol: "ETH",
      price: prices.ethereum.usd,
      change: prices.ethereum.usd_24h_change,
